import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { Route, Router, Routes, useNavigate } from 'react-router-dom';
import {
    Home,
    Register,
    Login,

} from './components';

const App = () => {
  const [posts, setPosts] = useState([]);
  const [token, setToken] = useState('');
  const [user, setUser] = useState({});
  
  const navigate = useNavigate();

  function logout() {
      window.localStorage.removeItem('token');
      setToken('');
      setUser({});
  }

  async function fetchPosts() {
      const results = await getPosts(token)
      setPosts(results.data.posts);
  }

  async function getMe() {
      const storedToken = window.localStorage.getItem('token');

      if (!token) {
          if (storedToken) {
              setToken(storedToken);
          }
          return;
      }

      const results = await getUserDetails(token)
      console.log(results)
      if (results.success) {
          setUser(results.data);
      } else {
          console.log(results.error.message);
      }
  }

  useEffect(() => {
      fetchPosts()
  }, [token])

  useEffect(() => {
      getMe();
  }, [token])


  return (
      <div>
          <Navbar logout={logout} token={token} />
          <Routes>
              <Route
                  path='/'
                  element={<Home 
                  />}
              />
              <Route
                  path='/posts'
                  element={<Posts
                      token={token}
                      posts={posts}
                      fetchPosts={fetchPosts}
                  />}
              />
              <Route
                  exact path='/posts/create-post'
                  element={<CreatePost
                      token={token}
                      fetchPosts={fetchPosts}
                      navigate={navigate}
                  />}
              />
              <Route
                  exact path='/posts/edit-post/:postID'
                  element={<EditPost
                      posts={posts}
                      token={token}
                  />}
              />
              <Route
                  path='/posts/:postID'
                  element={<SinglePostView
                      posts={posts}
                      token={token}   
                  />}
              />
              <Route
                  path='/profile'
                  element={<Profile 
                      user={user}
                      token={token}
                  />}
              />
              <Route
                  path='/singlePostView'
                  element={<SinglePostView
                      user={user}
                      token={token}
                      posts={posts}
                  />}
              />
              <Route
                  path='/searchItems'
                  element={<SearchItems
                  posts={posts}
                  />}
              />
              <Route
                  path='/register'
                  element={<Register
                      setToken={setToken}
                      token={token}
                      navigate={navigate}
                  />}
              />
              <Route
                  path='/login'
                  element={<Login
                      setToken={setToken}
                      navigate={navigate}
                  />}
              />
          </Routes>
      </div>
  )
}

const root = ReactDOM.createRoot(document.getElementById("app"));
root.render(
  <BrowserRouter>
      <App />
  </BrowserRouter>
);
